import React from "react";
import { Card, ListGroup, Row, Col } from "react-bootstrap";

function Product({ product }) {
  return (
    <Card className="my-3 p-3 rounded" style={{ width: "12rem" }}>
      <Card.Img style={{ height: "22rem" }} variant="top" src={product.image} />
      <Card.Body>
        <Card.Title as="div">
          <strong>
            <i>{product.name}</i>
          </strong>
        </Card.Title>
        <Card.Text as="h4"><i class="fas fa-indian-rupee-sign"></i>{product.price}</Card.Text>
        <Card.Text as="div">{product.description}</Card.Text>
        <ListGroup>
          <Row>
            <Col>
              Status :{" "}
              {product.countInStock > 0
                ? "HURRY UP TO PURCHASE"
                : "PRODUCT IS OUT OF STOCK"}
            </Col>
          </Row>
        </ListGroup>
      </Card.Body>
    </Card>
  );
}

export default Product;
