import React, { useState, useEffect } from 'react'
import { Card, ListGroup, Row, Col ,Button} from "react-bootstrap";

function Product({ product ,history,match}) {
  const [qty, setQty] = useState(1)
  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`)
}
  return (
    <Card className="my-3 p-3 rounded" style={{ width: "20rem" }}>
      <Card.Img style={{ height: "22rem" }} variant="top" src={product.image} />
      <Card.Body>
        <Card.Title as="div">
          <strong>
            <i>{product.name}</i>
          </strong>
        </Card.Title>
        <Card.Text as="h4">
          <i class="fas fa-indian-rupee-sign"></i>
          {product.price}
        </Card.Text>
        <Card.Text as="div">{product.description}</Card.Text>

        <Card.Text as="h4">
          Status :
          {product.countInStock > 0
            ? "HURRY UP TO PURCHASE"
            : "PRODUCT IS OUT OF STOCK"}
        </Card.Text>
        <ListGroup.Item>
          <Button
            onClick={addToCartHandler}
            className="btn-block"
            disabled={product.countInStock == 0}
            type="button"
          >
            Add to Cart
          </Button>
        </ListGroup.Item>
      </Card.Body>
    </Card>
  );
}

export default Product;
