import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Navbar, Nav, Container,  } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";

import { logout } from "../actions/userActions";

function Header() {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const dispatch = useDispatch();

  const logoutHandler = () => {
    dispatch(logout());
  };

  return (
    <header>
      <Navbar bg="light" variant="light" expand="lg" collapseOnSelect>
        <Container>
          <LinkContainer to="/">
            <Navbar.Brand>
              <i class="fas fa-ice-cream"></i><h1>Arun</h1> 
            </Navbar.Brand>
          </LinkContainer>

          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
              {userInfo ? (
                <Navbar title={userInfo.name} id="username">
                  <LinkContainer to="/profile">
                    <Nav.Link>
                      <i className="fa-solid fa-cog fa-spin"></i> Settings
                    </Nav.Link>
                  </LinkContainer>

                  <Nav.Link onClick={logoutHandler}>
                    <i class="fa-sharp fa-solid fa-arrow-right-from-bracket"></i>{" "}
                    Logout
                  </Nav.Link>
                </Navbar>
              ) : (
                <LinkContainer to="/login">
                  <Nav.Link>
                    <i className="fas fa-user"></i> Login
                  </Nav.Link>
                </LinkContainer>
              )}
              {userInfo && userInfo.isAdmin && (
                <Navbar title="Admin" id="adminmenue">
                  <LinkContainer to="/admin/userlist">
                    <Nav.Link>
                      <i class="fa-solid fa-screwdriver-wrench"></i> Admin
                    </Nav.Link>
                  </LinkContainer>
                </Navbar>
              )}
              &emsp;&emsp;&emsp;
              <marquee
                width="80%"
                direction="left"
                height="50px"
                scrollamount="20"
              >
                <h3>
                  <strong>“Find happiness within.”</strong> – Marble Slab Creamery ice cream and
                  dessert stores
                </h3>
              </marquee>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
}

export default Header;
